import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export default function Home() {
  const [products, setProducts] = useState([]);
  const [isError, setIsError] = useState(false);
  const [imageUrls, setImageUrls] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/product");
        setProducts(response.data);

        // Fetch product images
        const imageResponsePromises = response.data.map(async (product) => {
          const imageResponse = await axios.get(`http://localhost:8080/product/${product.id}/image`, { responseType: 'arraybuffer' });
          const blob = new Blob([imageResponse.data], { type: imageResponse.headers['content-type'] });
          const url = URL.createObjectURL(blob);
          return { id: product.id, url };  // Return id and url in an object
        });

        const imageUrlsArray = await Promise.all(imageResponsePromises);
        const newImageUrls = imageUrlsArray.reduce((acc, { id, url }) => {
          acc[id] = url;
          return acc;
        }, {});

        setImageUrls(newImageUrls);
        setIsLoading(false);
      } catch (error) {
        setIsError(true);
        setIsLoading(false);
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  if (isLoading) {
    return <div className="alert alert-info">Loading products...</div>;
  }

  return (
    <div className="grid" style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
      {products.map((product) => (
        <div
          className="card mb-1 d-flex content-center"
          key={product.id}
          style={{
            width: "300px",
            height: "500px",
            boxShadow: "0 4px 8px rgba(7, 56, 153, 0.1)",
            borderRadius: "10px",
            margin: "10px",
            display: "flex",
            flexDirection: "column",
            overflow: "hidden",
          }}
        >
          <Link to={`/productById/${product.id}`} style={{ textDecoration: "none", color: "inherit" }}>
            <div>
              <h5 className='card-title' style={{ margin: "0 0 10px 0", fontSize: "1.2rem" }}>
                {"~" + (product.name ? product.name.toUpperCase() : "Unnamed Product")}
              </h5>
              <span className='card-brand'>
                ~ <i style={{ fontStyle: "italic" }}>{product.brand || "Unknown Brand"}</i>
              </span>
            </div>
            <div>
              {imageUrls[product.id] ? (
                <img
                  className="left-column-img"
                  alt={product.imageName || "Image unavailable"}
                  src={imageUrls[product.id]}
                  style={{
                    height: "300px",
                    width: "auto",
                    objectFit: "contain",
                  }}
                />
              ) : (
                <div>Loading image...</div>
              )}
            </div>
            <div>
              <h5 className='card-text' style={{ fontWeight: "600", margin: "10px" }}>
                {"â‚¹" + product.price}
              </h5>
              <button
                className='btn btn-primary rounded-pill'
                style={{ width: "80%", marginTop: "15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.5)" }}
                onClick={(e) => {
                  e.preventDefault();
                  // Handle adding to cart
                }}
              >
                Add To Cart
              </button>
            </div>
          </Link>
        </div>
      ))}
    </div>
  );
}
