import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { Toggle } from '../user/Toggle';

export default function Home() {
  const [product, setProduct] = useState([]);
  const [isError, setIsError] = useState(false);
  const { id } = useParams();
  const [imageUrls, setImageUrls] = useState({});
  const[isDark,isSetDark]=useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/product");
        setProduct(response.data);
        console.log(response.data);

        const imageResponsePromises = response.data.map(async (product) => {
          const imageResponse = await axios.get(`http://localhost:8080/product/${product.id}/image`, { responseType: 'arraybuffer' });
          const blob = new Blob([imageResponse.data], { type: imageResponse.headers['content-type'] });
          const url = URL.createObjectURL(blob);
          setImageUrls(prevImageUrls => ({
            ...prevImageUrls,
            [product.id]: url
          }));
        });

        await Promise.all(imageResponsePromises);
      } catch (error) {
        setIsError(true);
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  return (
    <>
    <h1></h1>
    <div className="App" dark-theme={isDark?"dark":"light"}>     
    <Toggle
                isChecked={isDark}
                handleChange={()=>isSetDark(!isDark)}
                />       

    <div  style={{ display: "flex", flexWrap: "wrap", gap: "10px", backgroundColor:"danger" }}>
      {product.map((product) => (
        <div
          className="card mb-1 d-flex content-center"
          key={product.id}
          style={{
            width: "310px",
            height: "500px",
            margin: "90px",
            display: "flex",
            flexDirection: "column",
            overflow: "hidden",
          }}
        >           <Link to={`/productById/${product.id}`}
            style={{ textDecoration: "none", color: "inherit" }}>
            <div>
           <br />
              <h5 className='card-title' style={{ margin: "0 0 10px 0", fontSize: "1.2rem" }}>
                <b>{ (product.name ? product.name.toUpperCase() : "Unnamed Product")}</b>
              </h5>
              <span className='card-brand'>
                ~ <i style={{ fontStyle: "italic" }}>{product.brand || "Unknown Brand"}</i>
              </span>
            </div>
            <div>
              {imageUrls[product.id] ? (
                <img
                  className="left-column-img"
                  alt={product.imageName || "Image unavailable"}
                  src={imageUrls[product.id]}
                  style={{
                    height: "300px",
                    width: "auto",
                    objectFit: "contain",
                  }}
                />
              ) : (
                <div>Loading image...</div>
              )}
            </div>
            <div>
              <h5 className='card-text' style={{ fontWeight: "600", margin: "10px" }}>
                {"â‚¹" + product.price}
              </h5>
              <button className='btn btn-primary rounded-pill' style={{ width: "80%", marginTop: "15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.5)" }}
                onClick={(e) => {
                  e.preventDefault();
                }}>
                Add To Cart
              </button>
            </div>
          </Link>
        </div>
      ))}
    </div>
    </div>
</>
  );
}
