import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';

export default function Home() {
  const [products, setProducts] = useState([]);
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/product");
        setProducts(response.data);
        console.log(response.data);
      } catch (error) {
        setIsError(true);
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  const fetchImageUrl = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8080/product/${id}/image`, {
        responseType: 'arraybuffer', // Make sure to handle binary data
      });
      const base64Image = Buffer.from(response.data, 'binary').toString('base64');
      return `data:image/jpeg;base64,${base64Image}`; // Assuming image is in jpeg format, modify accordingly
    } catch (error) {
      console.error('Error fetching image:', error);
      return null; // Fallback in case of error
    }
  };

  return (
    <div className="grid" style={{ display: "flex", flexWrap: "wrap", gap: "20px", backgroundColor: "danger" }}>
      {products.map((product) => {
        const [imageUrl, setImageUrl] = useState(null); // Image state for each product

        useEffect(() => {
          const loadImage = async () => {
            const url = await fetchImageUrl(product.id);
            setImageUrl(url);
          };

          loadImage();
        }, [product.id]);

        return (
          <div
            className="card mb-3 d-flex content-center"
            key={product.id}
            style={{
              width: "300px",
              height: "300px",
              boxShadow: "0 4px 8px rgba(7, 56, 153, 0.1)",
              borderRadius: "10px",
              margin: "100px",
              display: "flex",
              flexDirection: "column",
              overflow: "hidden",
            }}
          >
            <Link to={`/productById/${product.id}`}
              style={{ textDecoration: "none", color: "inherit" }}>
              <div>
                <h5 className='card-title' style={{ margin: "0 0 10px 0", fontSize: "1.2rem" }}>
                  {product.name.toUpperCase()}
                </h5>
                <span className='card-brand'>
                  by<i style={{ fontStyle: "italic" }}>{product.brand}</i>
                </span>
              </div>
              <div>
                {imageUrl ? (
                  <img
                    className="left-column-img"
                    alt={product.name}
                    src={imageUrl}
                    style={{
                      height: "300px", // Set the height to whatever value you want (e.g., 200px)
                      width: "auto", // Keeps the aspect ratio intact
                      objectFit: "contain", // Ensures the image is resized without being distorted
                    }}
                  />
                ) : (
                  <div>Loading...</div> // Loading placeholder if image is not available
                )}
              </div>
              <div>
                <h5 className='card-text' style={{ fontWeight: "600", margin: "10px" }}>
                  {"$" + product.price}
                </h5>
                <button className='btn btn-primary rounded-pill' style={{ width: "80%", marginTop: "15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.5)" }}
                  onClick={(e) => {
                    e.preventDefault();
                  }}>
                  Add To Cart
                </button>
              </div>
            </Link>
          </div>
        );
      })}
    </div>
  );
}
