import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export default function Home() {
  const [products, setProducts] = useState([]);
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/product');
        const productsWithImages = response.data.map(product => ({
          ...product,
          imageUrl: `data:${product.imageType};base64,${Buffer.from(product.imageDate, 'binary').toString('base64')}`
        }));
        setProducts(productsWithImages);
        console.log(productsWithImages);
      } catch (error) {
        setIsError(true);
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  return (
    <div className="grid" style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', backgroundColor: 'danger' }}>
      {products.map((product) => (
        <div
          className="card mb-3 d-flex content-center"
          key={product.id}
          style={{
            width: '300px',
            height: '300px',
            boxShadow: '0 4px 8px rgba(7, 56, 153, 0.1)',
            borderRadius: '10px',
            margin: '100px',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
          }}
        >
          <Link to={`/productById/${product.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
            <div>
              <h5 className='card-title' style={{ margin: '0 0 10px 0', fontSize: '1.2rem' }}>
                {product.name.toUpperCase()}
              </h5>
              <span className='card-brand'>
                by<i style={{ fontStyle: 'italic' }}>{product.brand}</i>
              </span>
            </div>
            <div>
              {product.imageUrl ? (
                <img
                  className="left-column-img"
                  alt={product.name}
                  src={product.imageUrl}
                  style={{
                    height: '300px',
                    width: 'auto',
                    objectFit: 'contain',
                  }}
                />
              ) : (
                <div>Loading...</div>
              )}
            </div>
            <div>
              <h5 className='card-text' style={{ fontWeight: '600', margin: '10px' }}>
                {'$' + product.price}
              </h5>
              <button
                className='btn btn-primary rounded-pill'
                style={{ width: '80%', marginTop: '15px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)' }}
                onClick={(e) => {
                  e.preventDefault();
                }}
              >
                Add To Cart
              </button>
            </div>
          </Link>
        </div>
      ))}
    </div>
  );
}
