import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import AppContext from '../cart/Cart';

const Product = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const { data, addToCart, removeFromCart, cart, refreshData } =
    useContext(AppContext);

  const navigate = useNavigate();
  
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/product/${id}`);
        setProduct(response.data);
        console.log(response.data);
      } catch (error) {
        console.error("Error fetching product", error);
      }
    };

    const fetchImage = async () => {
      const res = await axios.get(`http://localhost:8080/product/${id}/image`, {
        responseType: "blob",
      });
      setImageUrl(URL.createObjectURL(res.data));
    };

    fetchProduct();
    fetchImage();
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const handleAddToCart = () => {
    addToCart(product);
    alert("Product added to cart");
  };

  return (
    <div>
      <h2>Product Details</h2>
      
      {/* Embedded CSS */}
      <style>
        {`
          .containers {
            display: flex;
            justify-content: space-between;
            gap: 20px; /* Adds space between image and content */
          }

          .left-column-img {
            width: 300px;
            height: 100%;
            object-fit: contain;
            margin-right: 20px;
          }

          .right-column {
            flex: 1;
          }

          .product-price {
            margin-top: 20px;
          }

          .cart-btn {
            padding: 10px 20px;
            font-size: 16px;
            cursor: pointer;
            border: none;
            background-color: #28a745;
            color: white;
          }

          .disabled-btn {
            background-color: #ccc;
            cursor: not-allowed;
          }

          .release-date {
            font-size: 14px;
            margin-top: 10px;
          }

          .update-button button {
            margin-right: 10px;
          }
        `}
      </style>
      
      <div className="containers">
        <img
          className="left-column-img"
          src={imageUrl}
          alt={product.imageName}
        />
        <div className="right-column">
          <div className="product-description">
            <span>{product.category}</span>
            <h1>{product.name}</h1>
            <h5>{product.brand}</h5>
            <p>{product.description}</p>
          </div>

          <div className="product-price">
            <span>{"$" + product.price}</span>
            <button
              className={`cart-btn ${!product.productAvailable ? "disabled-btn" : ""}`}
              disabled={!product.productAvailable}
              onClick={handleAddToCart}
            >
              {product.productAvailable ? "Add to cart" : "Out of Stock"}
            </button>
            <h6>
              Stock Available :{" "}
              <i style={{ color: "green", fontWeight: "bold" }}>
                {product.stockQuantity}
              </i>
            </h6>
            <p className="release-date">
              <h6>Product listed on:</h6>
              <i>{product.releaseDate}</i>
            </p>
          </div>

          <div className="update-button">
            <button className="btn btn-success btn-sm mx-3" type="button">
              Update
            </button>
            <button className="btn btn-danger btn-sm" type="button">
              Delete
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Product;
