import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';
import AppContext from '../cart/Cart';

export default function Home({selectedCategory}) {
  const { data, isError, addToCart, refreshData } = useContext(AppContext);
  const [product, setProduct] = useState([]);
 const [isErrors, setIsError] = useState(false);
  const { id } = useParams();
  const[isDataFetched,setISDataFetched]=useState(false);
     useEffect(()=>{
    if(!isDataFetched){
      refreshData();
      setISDataFetched(true);
   }
},[refreshData,isDataFetched])

  useEffect(()=>{
   if(data && data.length>0){                                      //checking if data exists and has a item
    const fetchImageAndUpdatedProducts = async()=>{                 //defining fetch image and update products
    const updatedProducts=await Promise.all(                    // promise.all func used to handle multiple asynchronus function
    data.map(async(product)=>{    
      try{         
    const response = await axios.get(
      `http://localhost:8080/product/${product.id/image}`,
      {responseType:"blob"}
    );
     const imageUrl =URL.createObjectURL(response.data);
     return{...product,imageUrl};
    }catch(error){
      console.error("Error fetching image and product Id",product.id,error);
      return { ...product, imageUrl: "placeholder-image-url" };
    }
    })
    );
    setProduct(updatedProducts); 
    };
    fetchImageAndUpdatedProducts();
   }
  },[data])

  let filteredProducts;

  if (selectedCategory) {
    filteredProducts = product.filter((product) => product.category === selectedCategory);
  } else {
    filteredProducts = product;
  }
  

  if (isErrors) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  return (
    <div className="grid" style={{ display: "flex", flexWrap: "wrap", gap: "20px", backgroundColor:"danger" }}>
   
      {product.map((product) => {
        return (
          <div>
            
            <Link to={`/productById/${product.id}`}
           style={{ textDecoration: "none", color: "inherit" }}>
            <div> 
              <h5 className='card-title' style={{ margin: "0 0 10px 0", fontSize: "1.2rem" }}>
                {product.name.toUpperCase()}
              </h5>
              <span className='card-brand'>
                by<i style={{ fontStyle: "italic" }}>{product.brand}</i>
              </span>
            </div>
            <div>'
              <h5 className='card-text' style={{fontWeight:"600",margin:"10px"}}>
             {"$"+product.price}
              </h5>
              <button className='btn btn-primary rounded-pill' style={{width:"80%",marginTop:"15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.5)" }}
              onClick={(e)=>{
                e.preventDefault();
              }}> 
                Add To Cart
              </button>
            </div>
            </Link>
          </div>
        );
      })}
    </div>
  );
}
