import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom';

export default function Home() {
  const [product, setProduct] = useState([]);
  const [isError, setIsError] = useState(false);
  const { id } = useParams();
  const [imageUrl,setImageUrl]=useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/product/${product.i}`);
        setProduct(response.data);
        console.log(response.data);
        
      } catch (error) {
        setIsError(true);
        console.error('Error fetching data:', error);
      }
    };
    const fetchImage = async()=>{
      const res= await axios.get(`http://localhost:8080/product/${id}/image`,
        {responseType:"blob"}
      );
      setImageUrl(URL.createObjectURL(res.data));
    };

    fetchData();
    fetchImage(); 

  }, []);

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  return (
    <div className="grid" style={{ display: "flex", flexWrap: "wrap", gap: "20px", backgroundColor:"danger" }}>
      {product.map((product) => {
        return (
          <div
            className="card mb-3 d-flex content-center"
            key={product.id}
            style={{
              width: "300px",
              height: "210px",
              boxShadow: "0 4px 8px rgba(7, 56, 153, 0.1)",
              borderRadius: "10px",
              margin: "100px",
              display: "flex",
              flexDirection: "column",
              overflow: "hidden",
            }}
          >
            <Link to={`/productById/${product.id}`}
           style={{ textDecoration: "none", color: "inherit" }}>
            <div> 
              <h5 className='card-title' style={{ margin: "0 0 10px 0", fontSize: "1.2rem" }}>
                {product.name.toUpperCase()}
              </h5>
              <span className='card-brand'>
                by<i style={{ fontStyle: "italic" }}>{product.brand}</i>
              </span>
            </div>
            <img
          className="left-column-img"
          src={imageUrl}
          alt={product.imageName}
          style={{
            height: "300px", // Set the height to whatever value you want (e.g., 200px)
            width: "auto", // Keeps the aspect ratio intact
            objectFit: "contain", // Ensures the image is resized without being distorted
          }}
        />  

            <div>'
              <h5 className='card-text' style={{fontWeight:"600",margin:"10px"}}>
             {"$"+product.price}
              </h5>
              <button className='btn btn-primary rounded-pill' style={{width:"80%",marginTop:"15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.5)" }}
              onClick={(e)=>{
                e.preventDefault();
              }}> 
                Add To Cart
              </button>
            </div>
            </Link>
          </div>
        );
      })}
    </div>
  );
}
