import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { Toggle } from '../user/Toggle';

export default function Home() {
  const [products, setProducts] = useState([]);
  const [isError, setIsError] = useState(false);
  const { id } = useParams();
  const [imageUrls, setImageUrls] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/product");
        setProducts(response.data);
        console.log(response.data);

        const imageResponsePromises = response.data.map(async (product) => {
          const imageResponse = await axios.get(`http://localhost:8080/product/${product.id}/image`, { responseType: 'arraybuffer' });
          const blob = new Blob([imageResponse.data], { type: imageResponse.headers['content-type'] });
          const url = URL.createObjectURL(blob);
          setImageUrls(prevImageUrls => ({
            ...prevImageUrls,
            [product.id]: url
          }));
        });

        await Promise.all(imageResponsePromises);
      } catch (error) {
        setIsError(true);
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  return (
    <div  style={{ display: "flex", flexWrap: "wrap", gap: "10px", justifyContent: "center", backgroundColor: "#f8f9fa" }}>
      {products.map((product) => (
        <div
          className="card mb-3"
          key={product.id}
          style={{
            width: "310px",
            margin: "20px",
            display: "flex",
            flexDirection: "column",
            border: "1px solid rgba(0,0,0,.125)",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)"
          }}
        >
          <Link to={`/productById/${product.id}`}
            style={{ textDecoration: "none", color: "inherit" }}>
            <div className="card-body">
              <h5 className="card-title">{product.name ? product.name.toUpperCase() : "Unnamed Product"}</h5>
              <h6 className="card-subtitle mb-2 text-muted">{product.brand || "Unknown Brand"}</h6>
            </div>
            <div className="card-img-top text-center" style={{ height: "200px" }}>
              {imageUrls[product.id] ? (
                <img
                  alt={product.imageName || "Image unavailable"}
                  src={imageUrls[product.id]}
                  style={{
                    height: "100%",
                    width: "auto",
                    objectFit: "contain",
                  }}
                />
              ) : (
                <div>Loading image...</div>
              )}
            </div>
            <div className="card-body">
              <h5 className="card-text">{"₹" + product.price}</h5>
              <button className="btn btn-primary btn-block"
                onClick={(e) => {
                  e.preventDefault();
                }}>
                Add To Cart
              </button>
            </div>
          </Link>
        </div>
      ))}
    </div>
  );
}
