import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"

const UpdateProduct=()=>{
  const{id}=useParams();
  const[product,setProduct]=useState({});
  const[image,setImage]=useState();
  const[updateProduct,setUpdatedProduct]=useState({
     id :null,
     name :"",
     description :"",
     brand:"",
     price:"",
     category:"",
     quantity:"",
     releaseDate:"",
     available:false
  })
    //first fetchById

    useEffect(()=>{
   const fetchProduct =async()=>{
        try{
        const response = await axios.get(
            `http://localhost:8080/product/${id}`
        );
        setProduct(response.data);

        const responseImage = await axios.get(              //fetch the image by id
            `http://localhost:8080/product/${id}/image`,
            {responseType:"blob"}
        );
          //convert url to a file
          const imageFile=await convertUrlToFile(responseImage.data,response.data.imageName)
          setImage(imageFile);
          setUpdatedProduct(response.data);
        }
        catch(error){
            console.log("Error Fetching product:",error);
        }
   };
      fetchProduct();
    },[id])


      useEffect(()=>{
        console.log("image Updated",image)
      },[image]);


      const convertUrlToFile = async(blobData,fileName)=>{
        const file=new File([blobData,fileName],{type:blobData.type});
        return file;
      }


      const handleSubmit=async(e)=>{
      e.preventDefault();
       console.log("images",image);
       console.log("product",updateProduct)
       const updatedProduct = new FormData();
        updatedProduct.append("imageFile",image);
        updatedProduct.append(
            "productDto",
            new Blob([JSON.stringify(updatedProduct)],{type:"application/json"})
        );
        console.log("formData",updatedProduct)
        axios
        .put(`http://localhost:8080/product/${id}`,updatedProduct,{
         headers:{
            "Content-Type":"multipart/form-data",
         },
        })
        .then((response)=>{
            console.log("Product updated successfully",updatedProduct);
            alert("Product updated successfully!")
    
        })
        .catch((error)=>{
            console.error("Error updating product:", error);
            console.log("product unsuccessfull update",updatedProduct)
            alert("Failed to update product. Please try again.");    
        });
      };

      const handleChange=(e)=>{
        const{name,value}=e.target;
        setUpdatedProduct({
            ...updateProduct,
              [name]:value,
        });
      };

      const handleImageChange=(e)=>{
        setImage(e.target.file[0]);
      };
    return (
                    <div className="container">
            <div className="center-container">
                <h1>Update Page</h1>
                <form className="row g-3 pt-5" onSubmit={handleSubmit}>
                    <div className="col-md-6">
                        <label className="form-label"><h6>Name</h6>
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder={product.name}
                            value={updateProduct.name}
                            onChange={handleChange}
                            name="name"
                        />
                    </div>
                     <div className="col-md-6">
                     <label className="form-label"><h6>Brand</h6>
                     </label>
                     <input
                     type="text"
                     name="brand"
                     className="form-control"
                     placeholder={product.brand}
                     value={updateProduct.brand} 
                     onChange={handleChange} 
                     id="brand"/>
                    </div>
                    <div className="col-md-6">
                      <label className="form-label"><h6>Description:</h6></label>
                    <input 
                     type="text"
                     className="form-control"
                     name="description"
                     placeholder={product.description}
                     value={updateProduct.description}
                     onChange={handleChange}
                     id="description"
                     />
                    </div>
                    <div className="col-md-6"> 
                   <label className="form-label"><h6>Price</h6></label>
                   <input 
                   type="number"
                   className="form-control"
                   onChange={handleChange}
                   value={updateProduct.price}
                   placeholder={product.price}
                   name="price"
                   id="price"
                   />
                    </div>
                    <div className="col-md-6">
                   <label className="form-label"><h6>Category:</h6></label>
                   <select
                   className="form-select"
                   value={updateProduct.category}
                   onChange={handleChange}
                   name="category"
                   id="category"
                    >
                    <option value="">Select Category</option>
                    <option value="laptop">Laptop</option>
                    <option value="headphone">Headphone</option>
                    <option value="mobile">Mobile</option>
                    <option value="electronics">Electronics</option>
                    <option value="toys">Toys</option>
                    <option value="fashion">Fashion</option>

                    </select>
                    </div>
                    <div className="col-md-6">
                    <label className="form-label">
                    <h6>Stock Quantity</h6>
                    </label>
               <input type="number"
                      className="form-control"
                      onChange={handleChange}
                      placeholder={product.quantity}
                      value={updateProduct.quantity}
                      name="quantity"
                      id="quantity"/>
                    </div>
                    <div className="col-md-6">
                     <label className="form-label">
                      <h6>Image</h6>
                     </label>
                     <img 
                     src={image ? URL.createObjectURL(image):"Image unavailable"}
                     alt={product.imageName}
                     style={{
                      width: "100%",
                      height: "180px",
                      objectFit: "cover",
                      padding: "5px",
                      margin: "0",
                    }}      
                   />
              <input
              className="form-control"
              type="file"
              onChange={handleImageChange}
              placeholder="Upload image"
              name="imageUrl"
              id="imageUrl"
            />
                    </div>
                    <div className="col-1">
                      <div className="form-check">
                   <input 
                   className="form-check-input"
                   type="checkbox"
                   name="available"
                   id="gridCheck"
                   checked={updateProduct.available} 
                   onChange={(e)=>
                    setUpdatedProduct({...updateProduct,available:e.target.checked})
                   }
                   />
              <label className="form-check-label">Product Available</label>
                      </div>
                    </div>
                    <div className="col-4 ">
            <button type="submit" className="btn btn-sm form-control mx-4 btn-success">
              Submit
            </button>
            </div>
                </form>
            </div>

        </div>

    );
};
export default UpdateProduct
