import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import AppContext from '../cart/Cart';

 const Product=()=> {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [imageUrl,setImageUrl]=useState("");
  const { data, addToCart, removeFromCart, cart, refreshData } =
  useContext(AppContext);

  const navigate=useNavigate();
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/product/${id}`);
        setProduct(response.data);
        console.log(response.data);  
      } catch (error) {
        console.error("Error fetching product", error);
      }
    };
    const fetchImage = async()=>{
      const res= await axios.get(`http://localhost:8080/product/${id}/image`,
        {responseType:"blob"}
      );
      setImageUrl(URL.createObjectURL(res.data));
    };
  
    fetchProduct(); 
    fetchImage(); 
  }, [id]);  
  if (!product) {
    return <div>Loading...</div>;
  }

  const handlAddToCart = () => {
    addToCart(product);
    alert("Product added to cart");
  };

  return (
    <div>
      <h2>Product Details</h2>
      <div className="containers"
                  style={{
                    width: "px",
                    height: "210px",
                    boxShadow: "0 4px 8px rgba(7, 56, 153, 0.1)",
                    borderRadius: "10px",
                    margin: "100px",
                    display: "flex",
                    flexDirection: "column",
                    overflow: "hidden",
                  }}
      >
        
      <img
          className="left-column-img"
          src={imageUrl}
          alt={product.imageName}
        />  
        <div className="right-column">
          <div className="product-description">
            <span>{product.category}</span>
            <h1>{product.name}</h1>
            <h5>{product.brand}</h5>
            <p>{product.description}</p>
          </div>

          <div className="product-price">
            <span>{"$" + product.price}</span>
            <button
              className={`cart-btn ${!product.productAvailable ? "disabled-btn" : ""} `}
              disabled={!product.productAvailable}
            >
              {product.productAvailable ? "Add to cart" : "Out of Stock"}
            </button>
            <h6>
              Stock Available :{" "}
              <i style={{ color: "green", fontWeight: "bold" }}>
                {product.stockQuantity}
              </i>
            </h6>
            <p className="release-date">
              <h6>Product listed on:</h6>
              <i>{product.releaseDate}</i>
            </p>
          </div>

          <div className="update-button">
            <button className="btn btn-success btn-sm mx-3" type="button">
              Update
            </button>
            <button className="btn btn-danger btn-sm" type="button">
              Delete
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Product
