import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function LoginUser() {
  let navigate=useNavigate();


  const [user,setUser]=useState({
    email:"",
    password:""
  });


  const[emailError,setEmailError]=useState("");
 const[isEmailValid,setisEmailValid]=useState(true);
 const {email,password}=user;

  const onInputChange =(e)=>{
    setUser({...user,[e.target.name]:e.target.value});
  }


  const onSubmit =async(e)=>{
    e.preventDefault();
   const result = await axios.post("http://localhost:8080/login",user);
   navigate("/")
  };

  const checkEmail= async()=>{

   const email = document.getElementById('email').value;
   const button = document.getElementById('button');
   const res = await axios.get(`http://localhost:8080/save/${email}`);
   if(res.data.includes("Yes Email has been Found")){
    setisEmailValid(true);
    setEmailError("");
    button.disabled=false;
   }else {
    setisEmailValid(false); 
    setEmailError("Email not found. Please register first.");
    button.disabled=true;
  }

  }

  return (
    <div className="d-flex align-items-center " style={{ height: '70vh' }}>
      <div className="container">
        <div className="row ">
          <h7 className='text-between text-capitalize'><i>Login Here:</i></h7>
          <div className="col-md-6 offset-md-3 border bg-light rounded p-4 mt-2" style={{ height: '40vh' }}>
            <form onSubmit={(e)=>onsubmit}>
              <div className="align-self-center">
                <div className="mb-3">
                  <label htmlFor="email" className="form-label d-flex justify-content-start">
                    Email:
                  </label>
                  <input
                    type="text"
                    name='email'
                    id='email'
                    className="form-control"
                    placeholder="Enter your email"
                    onChange={(e)=>onInputChange(e)}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="password" className="form-label d-flex justify-content-start">
                    Password:
                  </label>
                  <input
                    type="password"
                    name='password'
                    id='password'
                    className="form-control"
                    placeholder="Enter a Password"
                    onChange={(e)=>onInputChange(e)}
                  />
                </div>
                <div className="d-flex justify-content-sm-start">
                  <button type="submit" id="button" className="btn btn-sm btn-dark rounded-pill">
                    Login
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
