import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import AppContext from '../cart/Cart';

export default function Home({ selectedCategory }) {
  const { data, isError, addToCart, refreshData } = useContext(AppContext);
  const [products, setProducts] = useState([]); // Renamed to 'products' for clarity
  const [isDataFetched, setIsDataFetched] = useState(false); // State for tracking data fetch

  // Refresh data on mount
  useEffect(() => {
    if (!isDataFetched) {
      refreshData();
      setIsDataFetched(true);
    }
  }, [refreshData, isDataFetched]);

  // Fetch images and update products

  // Filter products based on selected category
  let filteredProducts = [];

  if (selectedCategory) {
    filteredProducts = products.filter((product) => product.category === selectedCategory);
  } else {
    filteredProducts = products;
  }

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  return (
    <div className="grid" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
      {filteredProducts.length === 0 ? (
        <h2 className="text-center"
         style={{ display: "flex", 
         justifyContent: "center",
          alignItems: "center" }}>
          No Products Available
        </h2>
      ) : (
        filteredProducts.map((product) => {
          const { id, brand, name, price, productAvailable, imageUrl } = product;
          return (
            <div className="card mb-3" key={id} style={{ width: "18rem", height: "24rem", boxShadow: "rgba(0, 0, 0, 0.24) 0px 2px 3px", margin: "10px" }}>
              <Link to={`/product/${id}`} style={{ textDecoration: "none", color: "inherit" }}>
                <img
                  src={imageUrl}
                  alt={name}
                  style={{ width: "100%", height: "180px", objectFit: "cover", padding: "5px" }}
                />
                <div className="card-body" style={{ display: "flex", flexDirection: "column", justifyContent: "space-between", padding: "10px" }}>
                  <div>
                    <h5 className="card-title" style={{ margin: "0 0 10px 0" }}>{name.toUpperCase()}</h5>
                    <i className="card-brand" style={{ fontStyle: "italic" }}>{"~ " + brand}</i>
                  </div>
                  <div>
                    <h5 className="card-text" style={{ fontWeight: "600", margin: "5px 0" }}>{"$" + price}</h5>
                    <button
                      className="btn btn-primary"
                      style={{ width: "100%" }}
                      onClick={(e) => {
                        e.preventDefault();
                        addToCart(product);
                      }}
                      disabled={!productAvailable}
                    >
                      {productAvailable ? "Add to Cart" : "Out of Stock"}
                    </button>
                  </div>
                </div>
              </Link>
            </div>
          );
        })
      )}
    </div>
  );
}
