import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const UpdateProduct = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [image, setImage] = useState();
  const navigate = useNavigate();
  const [updateProduct, setUpdatedProduct] = useState({
    id: null,
    name: "",
    description: "",
    brand: "",
    price: "",
    category: "",
    quantity: "",
    releaseDate: "",
    available: false,
  });

  // Fetch product details by id
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/product/${id}`);
        setProduct(response.data);

        const responseImage = await axios.get(`http://localhost:8080/product/${id}/image`, {
          responseType: "blob",
        });

        const imageFile = await convertUrlToFile(responseImage.data, response.data.imageName);
        setImage(imageFile);
        setUpdatedProduct(response.data);
      } catch (error) {
        console.log("Error Fetching product:", error);
      }
    };

    fetchProduct();
  }, [id]);

  const convertUrlToFile = async (blobData, fileName) => {
    const file = new File([blobData], fileName, { type: blobData.type });
    return file;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("images", image);
    console.log("product", updateProduct);

    const updatedProductFormData = new FormData();
    updatedProductFormData.append("imageFile", image);
    updatedProductFormData.append("productDto", JSON.stringify(updateProduct)); // Correct way to append product data

    try {
      const response = await axios.put(`http://localhost:8080/product/${id}`, updatedProductFormData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log("Product updated successfully", response);
      alert("Product updated successfully!");
      navigate("/");
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Failed to update product. Please try again.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedProduct({ ...updateProduct, [name]: value });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <div className="container">
      <div className="center-container">
        <h1>Update Product</h1>
        <form className="row g-3 pt-5" onSubmit={handleSubmit}>
          <div className="col-md-6">
            <label className="form-label">
              <h6>Name</h6>
            </label>
            <input
              type="text"
              className="form-control"
              placeholder={product?.name || "Product Name"}
              value={updateProduct.name}
              onChange={handleChange}
              name="name"
            />
          </div>

          {/* Other fields (brand, description, etc.) */}
          <div className="col-md-6">
            <label className="form-label">
              <h6>Image</h6>
            </label>
            <img
              src={image ? URL.createObjectURL(image) : "/path/to/default-image.jpg"}
              alt={product?.imageName || "Product Image"}
              style={{
                width: "100%",
                height: "180px",
                objectFit: "cover",
                padding: "5px",
                margin: "0",
              }}
            />
            <input
              className="form-control"
              type="file"
              onChange={handleImageChange}
              placeholder="Upload image"
              name="imageUrl"
              id="imageUrl"
            />
          </div>

          <div className="col-4">
            <button type="submit" className="btn btn-sm form-control mx-4 btn-success">
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UpdateProduct;
