import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import AppContext from "../Context/Context";

export default function Home({ selectedCategory }) {
  const [products, setProducts] = useState([]);
  const [imageUrls, setImageUrls] = useState({});
  const [isError, setIsError] = useState(false);
  const { addToCart } = useContext(AppContext);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:8080/product");
        setProducts(response.data);

        const imagePromises = response.data.map(async (p) => {
          const imgRes = await axios.get(
            `http://localhost:8080/product/${p.id}/image`,
            { responseType: "arraybuffer" }
          );
          const blob = new Blob([imgRes.data], {
            type: imgRes.headers["content-type"],
          });
          return { id: p.id, url: URL.createObjectURL(blob) };
        });

        const imageData = await Promise.all(imagePromises);
        const imageMap = imageData.reduce((acc, { id, url }) => {
          acc[id] = url;
          return acc;
        }, {});
        setImageUrls(imageMap);
      } catch (error) {
        console.error("Error fetching products:", error);
        setIsError(true);
      }
    };

    fetchProducts();
  }, []); // ✅ Runs only once

  if (isError) {
    return (
      <div className="alert alert-danger">
        There was an error fetching the products.
      </div>
    );
  }

  const filteredProducts = selectedCategory
    ? products.filter((p) => p.category === selectedCategory)
    : products;

  return (
    <div className="grid">
      {filteredProducts.length === 0 ? (
        <h2 className="text-center">No Products Available</h2>
      ) : (
        filteredProducts.map((product) => {
          const { id, brand, name, price, available } = product;
          const imageUrl = imageUrls[id];

          return (
            <div className="card mb-3" style={{ width: "18rem" }} key={id}>
              <Link to={`/productById/${id}`} style={{ textDecoration: "none" }}>
                <img
                  src={imageUrl || "/placeholder.jpg"}
                  alt={name}
                  style={{ width: "100%", height: "180px", objectFit: "cover" }}
                />
                <div className="card-body">
                  <h5>{name.toUpperCase()}</h5>
                  <i>{"~ " + brand}</i>
                  <h5>{"₹" + price}</h5>
                  <button
                    className="btn btn-primary"
                    style={{ width: "100%" }}
                    onClick={(e) => {
                      e.preventDefault();
                      addToCart(product);
                    }}
                    disabled={!available}
                  >
                    {available ? "Add to Cart" : "Out of Stock"}
                  </button>
                </div>
              </Link>
            </div>
          );
        })
      )}
    </div>
  );
}
