import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";


const UpdateProduct = () => {
    const { id } = useParams();
    const [product, setProduct] = useState({});
    const [image, setImage] = useState();
    const [updateProduct, setUpdatedProduct] = useState({
        id: null,
        name: "",
        description: "",
        brand: "",
        price: "",
        category: "",
        quantity: "",
        releaseDate: "",
        available: false,
    });

    // First fetchById
    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/product/${id}`);
                setProduct(response.data);

                const responseImage = await axios.get(
                    `http://localhost:8080/product/${id}/image`,
                    { responseType: "blob" }
                );
                // Convert URL to a file
                const imageFile = await convertUrlToFile(responseImage.data, response.data.imageName);
                setImage(imageFile);
                setUpdatedProduct(response.data);
            } catch (error) {
                console.log("Error Fetching product:", error);
            }
        };
        fetchProduct();
    }, [id]);

    useEffect(() => {
        console.log("image updated", image);
    }, [image]);

    const convertUrlToFile = async (blobData, fileName) => {
        const file = new File([blobData], fileName, { type: blobData.type });
        return file;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("images", image);
        console.log("product", updateProduct);

        const formData = new FormData();
        formData.append("imageFile", image);
        formData.append(
            "product",
            new Blob([JSON.stringify(updateProduct)], { type: "application/json" })
        );

        console.log("formData", formData);

        try {
            await axios.put(`http://localhost:8080/product/${id}`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });
            console.log("Product updated successfully");

            alert("Product updated successfully!");
        } catch (error) {
            console.error("Error updating product:", error);
            alert("Failed to update product. Please try again.");
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedProduct({
            ...updateProduct,
            [name]: value,
        });
    };

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    return (
        <div className="container">
            <div className="center-container">
                <h1>Update Product</h1>
                <form className="row g-3 pt-5" onSubmit={handleSubmit}>
                    <div className="col-md-6">
                        <label className="form-label"></label>
                        <h6>Name</h6>
                        <input
                            type="text"
                            className="form-control"
                            placeholder={product.name}
                            value={updateProduct.name}
                            onChange={handleChange}
                            name="name"
                        />
                    </div>
                </form>
            </div>
        </div>
    );
};

export default UpdateProduct;
