import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
export default function Product() {
    useEffect(() => {
        const fetchProduct = async () => {
          try {
            const response = await axios.get(`http://localhost:8080/product/${id}`);
            setProduct(response.data);  // Set the fetched product data
            console.log(response.data);  // Log the response for debugging
          } catch (error) {
            console.error("Error fetching product", error);
          }
        };
    
        fetchProduct();  // Fetch the product on mount and whenever `id` changes
      }, [id]);  // Re-run when `id` changes
    
      // Return loading state or the actual product details
      if (!product) {
        return <div>Loading...</div>;
      }
    
  return (
    <div>Produsssssssssssssssssssssct

<div className="containers">
        <div className="right-column">
          <div className="product-description">
            <span>{product.category}</span>
            <h1>{product.name}</h1>
            <h5>{product.brand}</h5>
            <p>{product.description}</p>
          </div>

          <div className="product-price">
            <span>{"$" + product.price}</span>
            <button
              className={`cart-btn ${!product.productAvailable ? "disabled-btn" : ""}`}
              disabled={!product.productAvailable}
            >
              {product.productAvailable ? "Add to cart" : "Out of Stock"}
            </button>
            <h6>
              Stock Available :{" "}
              <i style={{ color: "green", fontWeight: "bold" }}>
                {product.stockQuantity}
              </i>
            </h6>
            <p className="release-date">
              <h6>Product listed on:</h6>
              <i>{product.releaseDate}</i>
            </p>
          </div>
          <div className="update-button ">
            <button
              className="btn btn-primary"
              type="button"
          
            >
              Update
            </button>
        
            <button
              className="btn btn-primary"
              type="button"
            >
              Delete
            </button>
          </div>
        </div>
      </div>

    </div>
  )
}
