import React, { useContext, useState, useEffect } from "react";
import AppContext from "../Context/Context";
import axios from "axios";
import CheckoutPopup from "./CheckoutPopup";
import { Button } from 'react-bootstrap';

const Cart = () => {
  const { cart, removeFromCart, clearCart } = useContext(AppContext);
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [showModal, setShowModal] = useState(false);

  // Fetch product images and update cart items
  useEffect(() => {
    const fetchImagesAndUpdateCart = async () => {
      try {
        // Get product details from backend
        const response = await axios.get("http://localhost:8080/product");
        const backendProducts = response.data; // all products from the backend
        
        const updatedCartItems = cart.map((item) => {
          const product = backendProducts.find((p) => p.id === item.id);
          if (product) {
            return { ...item, ...product }; // Merge product details
          }
          return item;
        });

        // Get product images
        const cartItemsWithImages = await Promise.all(
          updatedCartItems.map(async (item) => {
            try {
              const imageResponse = await axios.get(
                `http://localhost:8080/product/${item.id}/image`,
                { responseType: "arraybuffer" } // Use arraybuffer to fetch image
              );
              const imageBlob = new Blob([imageResponse.data], { type: 'image/jpeg' });
              const imageUrl = URL.createObjectURL(imageBlob);
              return { ...item, imageUrl };
            } catch (error) {
              console.error("Error fetching image for product:", error);
              return { ...item, imageUrl: "placeholder-image-url" }; // Fallback image URL
            }
          })
        );
        
        setCartItems(cartItemsWithImages);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    };

    if (cart.length) {
      fetchImagesAndUpdateCart();
    }
  }, [cart]);

  // Recalculate total price
  useEffect(() => {
    const total = cartItems.reduce(
      (acc, item) => acc + item.price * item.quantity,
      0
    );
    setTotalPrice(total);
  }, [cartItems]);

  const handleIncreaseQuantity = (itemId) => {
    const newCartItems = cartItems.map((item) => {
      if (item.id === itemId && item.quantity < item.stockQuantity) {
        return { ...item, quantity: item.quantity + 1 };
      }
      return item;
    });
    setCartItems(newCartItems);
  };

  const handleDecreaseQuantity = (itemId) => {
    const newCartItems = cartItems.map((item) => 
      item.id === itemId
        ? { ...item, quantity: Math.max(item.quantity - 1, 1) }
        : item
    );
    setCartItems(newCartItems);
  };

  const handleRemoveFromCart = (itemId) => {
    removeFromCart(itemId);
    const newCartItems = cartItems.filter((item) => item.id !== itemId);
    setCartItems(newCartItems);
  };

  const handleCheckout = async () => {
    try {
      for (const item of cartItems) {
        const { imageUrl, imageType, quantity, ...productData } = item;
        const updatedStockQuantity = item.stockQuantity - item.quantity;

        const updatedProductData = { ...productData, stockQuantity: updatedStockQuantity };
        const cartProduct = new FormData();
        cartProduct.append("imageFile", imageUrl); // Add image file to formData
        cartProduct.append(
          "productDto",
          new Blob([JSON.stringify(updatedProductData)], { type: "application/json" })
        );

        await axios.put(`http://localhost:8080/product/${item.id}`, cartProduct, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });
      }
      
      clearCart();
      setCartItems([]);
      setShowModal(false);
    } catch (error) {
      console.log("Error during checkout", error);
    }
  };

  return (
    <div className="cart-container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', padding: '2rem' }}>
  <div className="shopping-cart" style={{ width: '100%', maxWidth: '960px', padding: '1.5rem', backgroundColor: '#fff', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', borderRadius: '8px' }}>
    <div className="title" style={{ fontSize: '1.8rem', fontWeight: 'bold', marginBottom: '1.5rem', color: '#333' }}>
      Shopping Bag
    </div>

    {cartItems.length === 0 ? (
      <div className="empty" style={{ textAlign: 'center', padding: '2rem' }}>
        <h4>Your cart is empty</h4>
      </div>
    ) : (
      <>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {cartItems.map((item) => (
            <li key={item.id} className="cart-item" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1.5rem', padding: '1rem', borderBottom: '1px solid #f0f0f0' }}>
              <div className="item" style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                {/* Image Section */}
                <div className="image" style={{ marginRight: '1rem', flexShrink: 0 }}>
                  <img
                    src={item.imageUrl || "placeholder-image-url"}
                    alt={item.name}
                    style={{ height: "100px", width: "100px", objectFit: "cover", borderRadius: '8px' }}
                    className="cart-item-image"
                  />
                </div>

                {/* Product Info Section */}
                <div className="description" style={{ flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'center', marginRight: '1rem' }}>
                  <span style={{ fontWeight: '600', color: '#333' }}>{item.brand}</span>
                  <span style={{ fontSize: '1rem', color: '#555' }}>{item.name}</span>
                </div>

                {/* Quantity Control Section */}
                <div className="quantity" style={{ display: 'flex', alignItems: 'center', marginRight: '1rem' }}>
                  <button
                    className="plus-btn"
                    type="button"
                    onClick={() => handleIncreaseQuantity(item.id)}
                    style={{
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '1.2rem',
                      padding: '5px',
                    }}
                  >
                    <i className="bi bi-plus-square-fill" style={{ color: '#00b894' }}></i>
                  </button>
                  <input
                    type="text"
                    value={item.quantity}
                    readOnly
                    style={{
                      width: '40px',
                      textAlign: 'center',
                      border: '1px solid #ddd',
                      borderRadius: '4px',
                      margin: '0 10px',
                      fontSize: '1rem',
                    }}
                  />
                  <button
                    className="minus-btn"
                    type="button"
                    onClick={() => handleDecreaseQuantity(item.id)}
                    style={{
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '1.2rem',
                      padding: '5px',
                    }}
                  >
                    <i className="bi bi-dash-square-fill" style={{ color: '#e74c3c' }}></i>
                  </button>
                </div>

                {/* Total Price Section */}
                <div className="total-price" style={{ textAlign: 'center', fontWeight: '600', color: '#333' }}>
                  ${item.price * item.quantity}
                </div>

                {/* Remove Button Section */}
                <div>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemoveFromCart(item.id)}
                    style={{
                      background: 'none',
                      border: 'none',
                      color: '#e74c3c',
                      cursor: 'pointer',
                    }}
                  >
                    <i className="bi bi-trash3-fill"></i>
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>

        <div className="total" style={{ marginTop: '2rem', fontSize: '1.2rem', fontWeight: '600', textAlign: 'right', color: '#333' }}>
          Total: ${totalPrice}
        </div>

        <Button
          className="btn btn-primary"
          style={{
            width: '100%',
            marginTop: '1.5rem',
            backgroundColor: '#00b894',
            borderColor: '#00b894',
            fontSize: '1.1rem',
            padding: '12px',
            fontWeight: '600',
          }}
          onClick={() => setShowModal(true)}
        >
          Checkout
        </Button>
      </>
    )}
  </div>

  <CheckoutPopup
    show={showModal}
    handleClose={() => setShowModal(false)}
    cartItems={cartItems}
    totalPrice={totalPrice}
    handleCheckout={handleCheckout}
  />
</div>

  );
};

export default Cart;
