import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import 'bootstrap-icons/font/bootstrap-icons.css';
import { Link } from 'react-router-dom';

export default function NavBar({ onSelectCategory, onSearch }) {
  const getInitialTheme = () => {
    const storedTheme = localStorage.getItem("theme");
    return storedTheme ? storedTheme : "light-theme";
  };
  
  const [selectedCategory, setSelectedCategory] = useState("");
  const [theme, setTheme] = useState(getInitialTheme());
  const [searchQuery, setSearchQuery] = useState("");
  const categories = [
    "Laptop",
    "Headphone",
    "Mobile",
    "Electronics",
    "Toys",
    "Fashion",
  ];

  const toggleTheme = () => {
    const newTheme = theme === "dark-theme" ? "light-theme" : "dark-theme";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);
  };

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    onSelectCategory(category);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
    if (onSearch) {
      onSearch(e.target.value);
    }
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (onSearch) {
      onSearch(searchQuery);
    }
  };

  return (
    <nav className="navbar navbar-dark bg-dark">
      <div className="container-fluid d-flex justify-content-end my-2">
        <a href="/cart" className="nav-link d-flex align-items-center">
          <i className="bi bi-cart me-2"></i> Cart
        </a>
        <Link className="btn btn-sm btn-secondary btn rounded-pill mx-2" to="/register">Register</Link>
        <Link className="btn btn-sm btn-danger btn rounded-pill mx-2" to="/login">Login</Link>
        <Link className="btn btn-sm btn-success btn rounded-pill mx-2" to="/addProducts">AddProduct</Link>
        <Link className="btn btn-sm btn-success btn rounded-pill mx-2" to="/addToCart">Cart</Link>
        <button className="theme-btn" onClick={toggleTheme}>
          {theme === "dark-theme" ? (
            <i className="bi bi-moon-fill"></i>
          ) : (
            <i className="bi bi-sun-fill"></i>
          )}
        </button>

        <div className="dropdown">
          <button
            className="btn btn-primary dropdown-toggle btn-sm mx-2"
            type="button"
            id="dropdownMenuButton"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Category
          </button>
          <ul className="dropdown-menu">
            {categories.map((category) => (
              <li key={category}>
                <button
                  className="dropdown-item"
                  onClick={() => handleCategorySelect(category)}
                >
                  {category}
                </button>
              </li>
            ))}
          </ul>
        </div>

        <div className="col-md-2">
          <form onSubmit={handleSearchSubmit}>
            <input
              className="form-control form-control-sm"
              type="search"
              placeholder="Search"
              aria-label="Search"
              value={searchQuery}
              onChange={handleSearchChange}
            />
          </form>
        </div>
      </div>
    </nav>
  );
}
