import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

export default function Home() {
  const [product, setProduct] = useState([]);
  const [isError, setIsError] = useState(false);
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/product");
        setProduct(response.data);
        console.log(response.data);
      } catch (error) {
        setIsError(true);
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  if (isError) {
    return <div className="alert alert-danger">There was an error fetching the products.</div>;
  }

  return (
    <div className="grid" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
      {product.map((product) => {
        return (
          <div
            className="card mb-3 d-flex content-center"
            key={product.id}
            style={{
              width: "300px",
              height: "210px",
              boxShadow: "0 4px 8px rgba(7, 56, 153, 0.1)",
              borderRadius: "10px",
              margin: "100px",
              display: "flex",
              flexDirection: "column",
              overflow: "hidden",
            }}
          >
            <div> 
              <h5 className='card-title' style={{ margin: "0 0 10px 0", fontSize: "1.2rem" }}>
                {product.name.toUpperCase()}
              </h5>
              <span className='card-brand'>
                by<i style={{ fontStyle: "italic" }}>{product.brand}</i>
              </span>
            </div>
            <div>'
              <h5 className='card-text' style={{fontWeight:"600",margin:"10px"}}>
             {"$"+product.price}
              </h5>
              <button className='btn btn-primary' style={{width:"80%",marginTop:"15px",shaf}}
              onClick={(e)=>{
                e.preventDefault();
              }}> 
                Add To Cart
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
}
